package tests

import (
	"crynux_relay/blockchain"
	"crynux_relay/config"
	"crynux_relay/models"
	"image"
	"image/color"
	"image/png"
	"os"
	"path"
	"path/filepath"
	"strconv"

	"github.com/ethereum/go-ethereum/common/hexutil"
)

func CreateImage() *image.RGBA {
	width := 200
	height := 100

	upLeft := image.Point{
		X: 0,
		Y: 0,
	}
	lowRight := image.Point{
		X: width,
		Y: height,
	}

	img := image.NewRGBA(
		image.Rectangle{
			Min: upLeft,
			Max: lowRight,
		})

	// Colors are defined by Red, Green, Blue, Alpha uint8 values.
	cyan := color.RGBA{R: 100, G: 200, B: 200, A: 0xff}

	// Set color for each pixel.
	for x := 0; x < width; x++ {
		for y := 0; y < height; y++ {
			switch {
			case x < width/2 && y < height/2: // upper left quadrant
				img.Set(x, y, cyan)
			case x >= width/2 && y >= height/2: // lower right quadrant
				img.Set(x, y, color.White)
			default:
				// Use zero value.
			}
		}
	}

	return img
}

func CreateImageDataURL() string {
	return "data:image/png;base64,"
}

func prepareResultImagesForTask(task *models.InferenceTask, numImages int) (string, error) {
	appConfig := config.GetConfig()

	imageFolder := filepath.Join(
		appConfig.DataDir.InferenceTasks,
		task.GetTaskIdAsString(),
		"results",
	)

	if err := os.MkdirAll(imageFolder, 0700); err != nil {
		return "", err
	}

	for i := 0; i < numImages; i++ {
		filename := filepath.Join(imageFolder, strconv.Itoa(i)+".png")
		img := CreateImage()
		f, err := os.OpenFile(filename, os.O_WRONLY|os.O_CREATE, 0777)
		if err != nil {
			return "", err
		}

		if err := png.Encode(f, img); err != nil {
			return "", err
		}

		if err := f.Close(); err != nil {
			return "", err
		}
	}

	// Calculate the pHash for the images
	var result []byte

	for i := 0; i < 9; i++ {
		imageFilename := path.Join(
			appConfig.DataDir.InferenceTasks,
			task.GetTaskIdAsString(),
			"results",
			strconv.Itoa(i)+".png",
		)

		imageFile, err := os.Open(imageFilename)
		if err != nil {
			return "", err
		}

		pHash, err := blockchain.GetPHashForImage(imageFile)
		if err != nil {
			return "", err
		}

		result = append(result, pHash...)
	}

	return hexutil.Encode(result), nil
}
